# Parse the HTML content using BeautifulSoup
soup = BeautifulSoup(html_content, 'html.parser')

# Find all the anchor tags within the grid
anchors = soup.find_all('a')
max_length = 30
# Extract href and h1 name
for anchor in anchors:
    href = anchor['href']
    h1_names = anchor.find('h1')
    for h1 in h1_names:
        name = h1.text
        href=href.replace(" ","")
        print(f"{name} {"-"*(max_length-len(name))}-> https://vitap.ac.in{href}")
